    @InjectRepository(User)
    private readonly userRepository: EntityRepository<User>,
    @InjectRepository(Article)
    private readonly articleRepository: EntityRepository<Article>,
  ) {}

  async findRankedAuthors(): Promise<any[]> {
    const users = await this.userRepository.findAll();
    const userStats = await Promise.all(users.map(async (user) => {
      const articles = await this.articleRepository.find({ author: user });
      const totalFavorites = articles.reduce((sum, article) => sum + article.favoritesCount, 0);
      const firstArticleDate = articles.length > 0 ? articles[0].createdAt : null;
      return {
        username: user.username,
        articlesAuthored: articles.length,
        totalFavorites,
        firstArticleDate,
